mapper.select:
SELECT a.ASSET_ID, a.ASSET_NAME, b.RULE_RELA_ID, b.SEVERITY, b.FACILITY, b.MERGE_ITEMS, b.MERGE_WINDOW_TIME, b.EVENT_WINDOW_TIME, b.EVENT_THRESHOLD, c.ANALYSIS_RULE_ID, c.ANALYSIS_RULE_NAME, c.RULE_TYPE, c.METHOD_NAME, c.ITEM_SPLIT, c.KV_SPLIT, d.JAR_NAME, d.VERSION FROM sop_asset a LEFT JOIN sop_log_analysis_rule_rela b ON a.ASSET_ID = b.ASSET_ID LEFT JOIN sop_log_analysis_rule c ON b.ANALYSIS_RULE_ID = c.ANALYSIS_RULE_ID LEFT JOIN sop_log_analysis_customization_rule d ON c.METHOD_NAME = d.METHOD_NAME WHERE a.ASSET_STATUS = 2 AND a.DELETE_STATE = 0 AND b.DELETE_STATE = 0 AND c.DELETE_STATE = 0
AND a.ASSET_ID = #{assetId, jdbcType=VARCHAR}
AND c.RULE_TYPE = #{ruleType,jdbcType=VARCHAR}
AND b.SEVERITY = #{severity,jdbcType=VARCHAR}
AND b.FACILITY = #{facility,jdbcType=VARCHAR}

SELECT b.RULE_RELA_ID, a.ASSET_ID, a.ASSET_NAME, a.ASSET_CLASS, a.ASSET_TYPE, a.IP_ADDRESS, a.SERVICE_PORT, a.NM_PORT, a.NM_PROCOTOL, a.NM_ACCOUNT, a.NM_PASSWORD, a.ASSET_ORG, a.ASSET_MANAGER, b.ANALYSIS_RULE_ID, b.SEVERITY, b.FACILITY, b.MERGE_ITEMS, b.MERGE_WINDOW_TIME, b.EVENT_WINDOW_TIME, b.EVENT_THRESHOLD, c.RULE_TYPE, d.JAR_NAME, d.VERSION, c.METHOD_NAME, c.ITEM_SPLIT, c.KV_SPLIT FROM sop_asset a LEFT JOIN sop_log_analysis_rule_rela b ON a.ASSET_ID = b.ASSET_ID LEFT JOIN sop_log_analysis_rule c ON b.ANALYSIS_RULE_ID = c.ANALYSIS_RULE_ID LEFT JOIN sop_log_analysis_customization_rule d ON c.METHOD_NAME = d.METHOD_NAME WHERE a.ASSET_STATUS = 2 AND a.DELETE_STATE = 0 AND b.DELETE_STATE = 0 /*AND c.DELETE_STATE = 0*/
AND a.IP_ADDRESS = #{ip, jdbcType=VARCHAR}
AND b.FACILITY = #{facility, jdbcType=VARCHAR}
AND b.SEVERITY = #{severity, jdbcType=VARCHAR}
