mapper.select:
SELECT lat.RECORD_ID AS recordId, lat.ASSET_ORG AS assetOrg, lat.IP_ADDRESS AS ipAddress, lat.ENABLE_PORT AS enablePort, lat.ASSET_TYPE AS assetType, lat.ASSET_INFO AS assetInfo, lat.SCAN_TIME AS scanTime, CASE WHEN a.IP_ADDRESS IS NULL THEN 1 ELSE 0 END AS existsInAssetTable FROM sop_logical_asset_temp AS lat LEFT JOIN sop_asset AS a ON a.IP_ADDRESS = lat.IP_ADDRESS AND a.SERVICE_PORT=lat.ENABLE_PORT AND a.DELETE_STATE = 0
AND lat.ASSET_ORG = #{assetOrg}
AND lat.IP_ADDRESS like concat('%',#{ipAddress},'%')
AND lat.ENABLE_PORT = #{enablePort}
AND STR_TO_DATE(lat.SCAN_TIME, '%Y%m%d%H%i%s') >= #{beginScanTime}
AND STR_TO_DATE(lat.SCAN_TIME, '%Y%m%d%H%i%s') <= #{endScanTime}
ORDER BY lat.RECORD_ID DESC
