mapper.select:
SELECT pat.IP_ADDRESS AS ipAddress, pat.RECORD_ID AS recordId, pat.ASSET_ORG AS assetOrg, pat.ASSET_STATUS AS assetStatus, pat.TYPE AS type, STR_TO_DATE(pat.SCAN_TIME, '%Y%m%d%H%i%s') AS scanTime, CASE WHEN a.IP_ADDRESS IS NULL THEN 1 ELSE 0 END AS existsInAssetTable FROM sop_physical_asset_temp AS pat LEFT JOIN sop_asset AS a ON a.IP_ADDRESS = pat.IP_ADDRESS AND a.DELETE_STATE = 0 AND a.ASSET_CLASS = "1"
AND pat.ASSET_ORG = #{assetOrg}
AND pat.IP_ADDRESS like concat('%',#{ipAddress},'%')
AND pat.ASSET_STATUS = #{assetStatus}
AND STR_TO_DATE(pat.SCAN_TIME, '%Y%m%d%H%i%s') >= #{beginScanTime}
AND STR_TO_DATE(pat.SCAN_TIME, '%Y%m%d%H%i%s') <= #{endScanTime}
ORDER BY pat.RECORD_ID DESC

SELECT DISTINCT t.IP_ADDRESS AS ipAddress, t.RECORD_ID AS recordId, STR_TO_DATE(t.SCAN_TIME, '%Y%m%d%H%i%s') AS scanTime, t.ASSET_ORG AS assetOrg, t.TYPE AS type FROM sop_physical_asset_temp AS t LEFT JOIN sop_asset AS a ON t.IP_ADDRESS = a.IP_ADDRESS WHERE a.IP_ADDRESS IS NULL AND t.ASSET_STATUS = 1
AND t.IP_ADDRESS = #{ip}
ORDER BY t.RECORD_ID DESC
